public boolean helper(String s,int start, int end,int deleted,int k){
        while(start<end){
            if(s.charAt(start)!=s.charAt(end)){
                if(deleted==k){
                    return false;
                }
                return helper(s,start+1,end,deleted+1,k) || helper(s,start,end-1,deleted+1,k);
            }else{
                start++;
                end--;
            }
        }
        return true;
    }
    public boolean validPalindrome(String s) {
        int k=1;    //at most k character delete
        return helper(s,0,s.length()-1,0,k);

    }
