``` cpp
    bool validPalindrome(string s, bool fixed = false) {
        int l(0), r(s.size() - 1);
        while(l < r){
            if(s[l] != s[r]){
                if(fixed) return false;
                fixed = true;
                return validPalindrome(s.substr(l, r - l), fixed) || validPalindrome(s.substr(l + 1, r - l), fixed);
            }
            r--;
            l++;
        }
        return true;
    }
