class Solution {
public:
    bool validPalindrome(string s) {

        int n = s.size();
        if( n  ==1)
        {
             return n;
        }

int i = 0 , j = n-1;

int countI = 0;
int countJ= 0;
int storeI =0;
int storeJ =0;
    while( i<=j)
    {

      if( s[i]!=s[j])
      {
          if( !countI)
          {
              countI=1;
              storeI =i;
              storeJ =j;
              i++;
              continue;
          }

          if(!countJ)
          {
             i= storeI;
             j= storeJ; 
             countJ =1;
             j--;
             continue;
          }
          return 0;
      }

      i++;
      j--;
    }

return true ;
    }
};

