      int c=0;
      int j=s.length()-1,i=0;
      while(i<=j)
      {
          if(s[i]==s[j])
          {
              j--;
              i++;
          }
          else 
          {
              if(s[i]==s[j-1] )
              {
                  j=j-2;
                  c++;
                  i++;
              }
              else if(s[i+1]==s[j])
              {
                  i=i+2;
                  c++;
                  j--;
              }
              else{
                  return false;
              }
          }

      }

return c<=1;

    } 
