		ListNode* removeElements(ListNode* head, int val) {
			if(head == NULL){
				return NULL;
			}
			if(head -> val != val){
				head -> next = removeElements(head -> next, val);
				return head;
			}
			else{
				ListNode* newHead = head -> next;
				return removeElements(newHead, val);
			}
		}
