class Solution {
public:
    ListNode* removeElements(ListNode* head, int val) {
        ListNode* prev = nullptr, *cur = head;
        while(cur) {
            if(cur->val == val) {
                if(cur == head)
                    head = head->next;
                else
                    prev->next = cur->next;
            }else {
                prev = cur;
            }
            cur = cur->next;
        }
        return head;
    }
};
