class Solution {
    public ListNode removeElements(ListNode head, int val) {
        ListNode temp = head;
        while(temp != null) {
            if(head.val==val && temp==head) {
                head= head.next;
                temp = head;
                continue;
            }
            if(temp.next==null) {
                break;
            }
            if(temp.next.val == val) {
                temp.next = temp.next.next;
                continue;
            }
            temp = temp.next;
        }
        return head;
    }
}
