var calPoints = function(ops) {
    const arr = []
    const actions = {
        C: () => arr.pop(),
        D: () => arr.push(arr[arr.length - 1] * 2),
        '+': () => arr.push(arr[arr.length - 1] + arr[arr.length - 2])
    }
    for (let a of ops) {
