class Solution {
public:
    int calPoints(vector<string>& ops) {
        int n = ops.size() , sum = 0 ;
		int k = 1; // we use this variable for the indexing of ans vector.
        vector<int> ans;
        ans.push_back(stoi(ops[0])); //it is certain that first element should be inserted for any other operation.
        for(int i = 1 ; i < n ;i ++) //we run a loop from index 1 to last.
        {
            if(ops[i] == "D")
            {
                ans.push_back(ans[k - 1] * 2);
                k ++;
            }
            else if(ops[i] == "C")
            {
                ans.pop_back();
                k --; //we decrement an index because we remove an element.
            }
            else if(ops[i] == "+")
