                ans.push_back(ans[k - 1] + ans[k - 2]);
                k ++;
            }
            else
            {
                ans.push_back(stoi(ops[i]));
                k ++;
            }
        }
        for(int j = 0 ; j < ans.size() ; j ++)
        {
            sum = sum + ans[j];
        }
        return sum;
    }
};
