impl Solution {
    pub fn cal_points(ops: Vec<String>) -> i32 {
        ops.iter()
            .fold(vec![], |mut acc, cur| {
                match cur.as_str() {
                    "+" => acc.push(acc[acc.len() - 1] + acc[acc.len() - 2]),
                    "D" => acc.push(acc[acc.len() - 1] * 2),
                    "C" => {
                        acc.pop();
                    }
                    x => acc.push(str::parse::<i32>(x).unwrap()),
                }
                acc
            })
            .iter()
            .sum()
    }
}
