public int calPoints(String[] ops) {
	Stack<Integer> s = new Stack<>();
	int total = 0, temp;
	for(String op : ops) {           //  for each loop for string array
		if(op.equals("C")) {
			total -= s.pop();
		}
		else if(op.equals("D")) {
			temp = 2*s.peek();
			s.push(temp);
			total += temp;
		}
