var calPoints = function(ops) {
    let output = [];
    let totalScore = 0;

    for (let i = 0; i < ops.length; i++) {
        const op = ops[i];

        if (!APPLY[op]) {
            output.push(op);
            totalScore += Number(op);
        } else if (op === "+") {
            const newScore = Number(output[output.length - 1]) + Number(output[output.length - 2]);
            output.push(newScore);
            totalScore += newScore;
        } else if (op === "D") {
            const newScore = Number(output[output.length - 1]) * 2
            output.push(newScore);
            totalScore += newScore;
        } else {
            totalScore -= output.pop();
        }
    }

    return totalScore;
};
