class Solution {
public:
    int calPoints(vector<string>& ops) {

        stack<int> s;
        int sum=0;
        for(int i=0;i<ops.size();i++)
        {
           if(ops[i]=="C")
               s.pop();

            else if(ops[i]=="D")
                s.push(2 * s.top());

            else if(ops[i]=="+")
            {
                int temp=s.top();
            s.pop();
                int temp2=s.top();
                s.push(temp);
                s.push(temp+temp2);
            }
            else
            {
                //converting string to int and then inserting it into stack
                s.push(stoi(ops[i]));
            }

        }

        int result=0;
        // calculating the total sum of the integers present in stack 
        while(!s.empty())
        {
            result+=s.top();
            s.pop();
        }
        return result;
    }
};

