class Solution:
    def calPoints(self, ops: List[str]) -> int:
        stack=[]
        for n in ops:
            if n=="+":
                stack.append(stack[-1]+stack[-2])
            elif n=="D":
                stack.append(stack[-1]*2)
            elif n=="C":
                stack.pop()
            else:
                num=int(n)
                stack.append(num)
        return sum(stack)  
