class Solution {
public:
    int calPoints(vector<string>& ops) 
    {
        stack<int> st;                  // it will store the values in integer format

        // storing all the values 
        for(int i=0;i<ops.size();i++)
        {
            if(ops[i]=="D")               // if it is "D" then store double as a new record
            {
                st.push(st.top()*2);
            }

            else if(ops[i]=="C")          // if it is "C" then delete previous record
            {
                st.pop();
            }

            else if(ops[i]=="+")          // if it is "+" then store sum of pervious two as a new record
            {
                int first = st.top();     // take out the first previous
                st.pop();

                int second = st.top();    // take out the second previous
                st.pop();

                st.push(second);          // again push values for making stack as it is
                st.push(first);
                st.push(first+second);    // store the sum as a new record
            }

            else                          // else store the value in integer form
            {
                st.push(stoi(ops[i]));
            }
        }

        int res = 0;
        while(!st.empty())                           // store all the sum in res
        {
            res = res + st.top();
            st.pop();
        }
        return res;
    }
};
