class Solution {
public:
    int calPoints(vector<string>& ops) {
        stack<int> st;
        int ans=0;
        for(auto x : ops){
            if(x=="+"){
                // CASE 1 
                // STORE TOP VALUE OF STACK IN TEMP1 THEN POP IT
                // THEN STORE THE TOP VALUE AGAIN IN TEMP2 
                //PUSH TEMP1 THAN PUSH TEMP1+TEMP2
                int temp1 = st.top();
                st.pop();
                int temp2 = st.top();
                st.push(temp1);
                st.push(temp1+temp2);
            }
            else
                if(x=="C"){
                    // CASE 2
                    // JUST POP FROM THE GIVEN STACK
                    st.pop();
                }
            else
                if(x=="D"){
                    // CASE 3
                    // PUSH TWICE THE VALUE OF TOP INTO THE STACK
                    st.push(st.top()*2);
                }
            else{
                st.push(stoi(x));
            }
        }
        // FIND SUM OF THE GIVEN STACK
        while(!st.empty()){
            ans+=st.top();
            st.pop();
        }
        return ans;
    }
};
