class Solution {
public:
    int calPoints(vector<string>& ops) {
        stack<int> ValueStep;
        stack<int> Step;
        for(int i=0;i<ops.size();i++){
            if(ops[i]!="C" && ops[i]!="D" && ops[i]!="+"){
                int point=stoi(ops[i]);
                if(ValueStep.empty()){
                    ValueStep.push(point); 

                    Step.push(point);
                }
                else{
                    int sum=ValueStep.top()+point;
                    ValueStep.push(sum);

                    Step.push(point);
                }
            }
            else if(ops[i]=="C"){
                int minus=ValueStep.top()-Step.top();
                ValueStep.push(minus);
                Step.pop();
            }
            else if(ops[i]=="D"){
                int Double=Step.top()*2;
                ValueStep.push(Double+ValueStep.top());
                Step.push(Double);
            }
            else{
                int ValueSteptop=Step.top();
                Step.pop();
                int sum2step=ValueSteptop+Step.top();
                ValueStep.push(sum2step+ValueStep.top());
                Step.push(ValueSteptop);
                Step.push(sum2step);
            }
        }
        return ValueStep.top();
    }
};
