     public int calPoints(String[] ops) {
        if(ops == null || ops.length == 0){
            return  0;}

        Deque<Integer> stack = new LinkedList<>();
        int sum = 0;
        //has to use .equals not ==
        for(String ccc: ops){
            if(isdigit(ccc)){
                int temp = Integer.parseInt(ccc);
                stack.offerFirst(temp);
                sum += temp;
            }else if(ccc.equals("D")){
                if(!stack.isEmpty()){
                    int c = stack.peekFirst()*2;
                    sum += c;
                    stack.offerFirst(c);
                }
            }else if(ccc.equals("C")){
                if(!stack.isEmpty()){
                    int remove = stack.removeFirst();
                    sum -= remove;
                }
            }else if(ccc.equals("+")){
                if(!stack.isEmpty()){  
                    int addmid = stack.removeFirst();
                    if(!stack.isEmpty()){
                        int addfirst = stack.removeFirst();
                        int curr = addfirst + addmid;
                        sum += curr;
                        stack.offerFirst(addfirst);
                        stack.offerFirst(addmid);
                        stack.offerFirst(curr);
                        continue;
                    }
                    sum += addmid;
                    stack.offerFirst(addmid);
                    stack.offerFirst(addmid);
