    int calPoints(vector<string>& op) {
        vector<int> ans;

        for(int i = 0; i < op.size(); i++){
            if(op[i] == "C"){
                ans.pop_back();
            }
            else if(op[i] == "D"){
                int val = ans.back();
                ans.push_back(2*val);
            }
            else if(op[i] == "+"){
                int val1 = ans.back();
                ans.pop_back();
                int val2 = ans.back();
                ans.push_back(val1);
                ans.push_back(val1 + val2);
            }
