public class Solution {
    public int CalPoints(string[] operations) {
        int length = operations.Length;
        int[] res = new int[length];
        Stack<int> myStack = new Stack<int>(res);
        int ans=0;
        int i=0;
        while(i<length){
            if(operations[i]=="+"){
                if (myStack.Count >= 2){
                    int firstElement = myStack.Pop();
                    int secondElement = myStack.Pop();
                    int sum = firstElement + secondElement;
                    myStack.Push(secondElement);
                    myStack.Push(firstElement);
                    myStack.Push(sum);
                }
            }
            else if(operations[i]=="C"){
                if (myStack.Count >= 1){
                    myStack.Pop();
                }
            }
            else if(operations[i]=="D"){
                 int top = myStack.Pop();
                 int result = top * 2;
                 myStack.Push(top);
                 myStack.Push(result);
            }else{
                myStack.Push(Int32.Parse(operations[i]));
            }
            i++;
        }
        while (myStack.Count > 0){
            ans+=myStack.Pop();
        }
        return ans;
    }
}
```

