**Approach 1**
**Iterative method**
```
class Solution {
public:
    ListNode* deleteDuplicates(ListNode* head) {
        ListNode* sentinel=new ListNode(0);
        sentinel->next=head;
        ListNode* pred=sentinel;
        if(!head)
        return NULL;
        while(head)
        {
            if(head->next!=NULL&&head->val==head->next->val)
            {
            while(head->next!=NULL&&head->val==head->next->val)
            {
                head=head->next;
            }
                pred->next=head->next;
            }
            else
            {
                pred=pred->next;
            }
            head=head->next;
        }
        return sentinel->next;
    }
};
```
**Approach 2**
**Recurcive solution**
```
class Solution {
public:
    ListNode* deleteDuplicates(ListNode* head) {
       if(!head)
       return NULL;
       if(!head->next)
       return head;
       int value=head->val;
        ListNode* curr=head->next;
        if(curr->val==value)
        {
            while(curr&&curr->val==value)
            {
                curr=curr->next;
            }
            return deleteDuplicates(curr);
        }
        else
        {
            head->next=deleteDuplicates(curr);
            return head;
        }
    }
};
```
**Approach 3**
**Creating new nodes**
```
class Solution {
public:
    ListNode* deleteDuplicates(ListNode* head) {
        if(!head)
        return NULL;
        unordered_map<int,int> freq;
        while(head)
        {
            freq[head->val]++;
            head=head->next;
        }
        vector<int> v;
        for(auto pr:freq)
        {
            if(pr.second==1)
            v.push_back(pr.first);
        }
        if(v.size()==0)
        return NULL;
        sort(v.begin(),v.end());
        ListNode* temp=new ListNode(v[0]);
        ListNode* p=temp;
        for(int i=1;i<v.size();i++)
        {
            temp->next=new ListNode(v[i]);
            temp=temp->next;
        }
        temp->next=NULL;
        return p;
    }
};
```
