ListNode* deleteDuplicates(ListNode* head) 
    {
		// Return head, if no nodes are present or only one node is present.
        if (!head || !head->next)
            return head;
		// Create a prev variable of ListNode type and initialize it with 0.
        ListNode* prev = new ListNode(0);
        ListNode* ans = prev; // To keep track of first element of the prev.
        ListNode* curr = head; 
		// Run the loop unless you find end of the loop or last node.
        while (curr && curr->next)
        {
			// Run another loop to skip the duplicate number.
            while (curr && curr->next && curr->val == curr->next->val)
            {
                int x = curr->val;
                while (curr && curr->val == x)
                    curr = curr->next;
            }
			// After skipping the nodes, join the prev node to the next of the last repeating or duplicate node.
            prev->next = curr;
            prev = prev->next;
            curr = (curr != NULL)? curr->next: NULL; // increment curr until it become NULL.
        }
		// Return next of ans, since prev is having a dummy value a the first node.
        return ans->next;
    }
	// Even after running two nested loops, each node is visited only ones, hence the solution is O(n) and space requirement is O(1).
