# Code
```
/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    ListNode* deleteDuplicates(ListNode* head) {
        map<int,int>mp;
        ListNode *p=head;
        while(p){
            mp[p->val]++;
            p=p->next;
        }
        ListNode *ans=new ListNode();
        ListNode *ans1=ans;
        for(auto it:mp){
            if(it.second==1){
                ans1->next=new ListNode(it.first);
                ans1=ans1->next;
            }
        }
        return ans->next;
    }
};
```

