class Solution {
    final char[] operators = {'+', '-', '*'};
    public List<String> addOperators(String num, int target) {
        Set<String> result = new HashSet<>();
        util(result, new StringBuilder(), num, target, -1, 0, num.length(), 3, 0, true);
        return result.stream().collect(Collectors.toList());
    }

    private void util(Set<String> result, StringBuilder sb, String num, int target, int current, int start, int len, int operatorLen, int prev, boolean isPositive) {
        if(current == target && start == len) {
            result.add(sb.toString());
            return;
        }
        for(int i = start; i < len; ++i) {
            String tempStr = num.substring(start, i + 1);
            if(tempStr.length() > 1 && tempStr.charAt(0) == '0') continue;
            if(Long.parseLong(tempStr) > Integer.MAX_VALUE) continue;
            int currentNum = Integer.parseInt(tempStr);
            for(int j = 0; j < operatorLen; ++j) {
                int sbLen = sb.length();
                int prevCurrent = current;
                int prevPrev = prev;
                boolean prevIsPositive = isPositive;
                if(sbLen > 0) {
                    sb.append(operators[j]);
                    switch(operators[j]) {
                        case '+':
                            current += currentNum;
                            prev = currentNum;
                            isPositive = true;
                            break;
                        case '-':
                            current -= currentNum;
                            prev = currentNum;
                            isPositive = false;
                            break;
                        case '*':
                            if(isPositive) {
                                current -= prev;
                                prev *= currentNum;
                                current += (prev);
                            } else {
                                current += prev;
                                prev *= currentNum;
                                current -= (prev);
                            }
                            break;
                    }
                } else {
                    current = currentNum;
                    prev = currentNum;
                }
                sb.append(tempStr);
                util(result, sb, num, target, current, i + 1, len, operatorLen, prev, isPositive);
                sb.setLength(sbLen);
                current = prevCurrent;
                prev = prevPrev;
                isPositive = prevIsPositive;
            }
        }
    }
}
