                solve(str, i+1, temp + "+" + s, curr, value + curr, target);
                solve(str, i+1, temp + "-" + s, -curr, value - curr, target);
                solve(str, i+1, temp + "*" + s, curr*last, (value - last) + (curr*last) , target);                
            }
        }
    }


    vector<string> addOperators(string str, int target) {

        solve(str,0,"",0,0,target);

