```C++
vector<string> addOperators(string num, int target) {
	vector<string> res;
	string cur = "";
	addOperators(num, 0, target, 0, 1, cur, res);
	return res;
}

void addOperators(string num, int index, int target, long val, long mult, string &cur,
				  vector<string> &res) {
	if (index == num.length()) {
		if (val == target) {
			res.push_back(cur);
		}
		return;
	}

	int len = cur.size();
	for (int i = index; i < num.length(); i++) {
		string sVal = num.substr(index, i - index + 1);
		long n = stol(sVal);
		if (index == 0) {
			cur += sVal;
			addOperators(num, i + 1, target, n, n, cur, res);
			cur.resize(len);
		} else {
			// +
			cur += "+" + sVal;
			addOperators(num, i + 1, target, val + n, n, cur, res);
			cur.resize(len);
			// -
			cur += "-" + sVal;
			addOperators(num, i + 1, target, val - n, -n, cur, res);
			cur.resize(len);
			// *
			cur += "*" + sVal;
			addOperators(num, i + 1, target, val - mult + mult * n, mult * n, cur, res);
			cur.resize(len);
		}
		if (n == 0) return;
	}
}
