    public List<String> addOperators(String num, int target) {
        List<String> res = new ArrayList<>();
        dfs(num, target, 0, "", 0, 0, res);
        return res;
    }

    private void dfs(String num, int target, int pos, String str, long sum, long last, List<String> res) {
        if (pos == num.length()) {
            if (sum == target) {
                res.add(str);
            }
            return;
        }
        for (int i = pos; i < num.length(); ++i) {
            long x = Long.valueOf(num.substring(pos, i + 1));
            if (pos == 0) {
                dfs(num, target, i + 1, str + x, x, x, res);
            } else {
                dfs(num, target, i + 1, str + "*" + x, sum - last + last * x, last * x, res);
                dfs(num, target, i + 1, str + "-" + x, sum - x, -x, res);
                dfs(num, target, i + 1, str + "+" + x, sum + x, x, res);
            }
            if (x == 0) {
                break;
            }
        }
    }
}
