                } else {
                    long plusValue = curr;
                    helper(num,i+1,soFar+plusValue,temp+"+"+curr,target,plusValue);

                    long minusValue = -curr;
                    helper(num,i+1,soFar+minusValue,temp+"-"+curr,target,minusValue);

                    long mulValue = prev*curr;
                    //subtract previously added value from mulValue
                    helper(num,i+1,soFar+mulValue - prev,temp+"*"+curr,target,mulValue);
                }
            }
        }
    }
