class Solution {
    func addOperators(_ num: String, _ target: Int) -> [String] {
        var res = [String]()
        let sArr = Array(num)
        func dfs(_ path: String, _ index: Int, _ value: Int, _ multed: Int) {
            if index == sArr.endIndex {
                if value == target {
                    res.append(path)    
                }
                return
