    void dfs(string &num, int target, int start, string str, long long sum, long long last) {
        if (num.size() == start) {
            if (target == sum) result.push_back(str);
            return;
        }

        long long x = 0;
        string ret = "";

        for (int i = start; i < num.size(); ++i) {
            x = x * 10 + num[i] - '0';
