class Solution {
    List<String> res = new ArrayList<>();
    long m, t;
    String num;
    public List<String> addOperators(String num, int target) {
        this.num = num;
        if (num == null || num.length() == 0) return res;
        m = num.length();
        t = target;
        dfs("", 0, 0, 0);
        return res;
    }

    private void dfs(String s, int idx, long sum, long mul) {
        if (idx == m) {
            if (sum == t) res.add(s);
            return;
        }
        for (int i = idx; i < m; i++) {
            if (i != idx && num.charAt(idx) == '0') break;  //invalid number format, for example, "03".
            long n = Long.parseLong(num.substring(idx, i+1));  // must use long to avoid overflow
            if (idx == 0) {    // first must be "+"
                dfs(n + "", i + 1, n, n);
                continue;
            }
            dfs(s + "+" + n, i + 1, sum + n, n);
            dfs(s + "-" + n, i + 1, sum - n, -n);
            dfs(s + "*" + n, i + 1, sum - mul + mul * n, mul * n);
