                dfs(res, i + 1, cur, sum + bufVal, path + "+" + cur, num, target);
                dfs(res, i + 1, -cur, sum + bufVal, path + "-" + cur, num, target);
                dfs(res, i + 1, bufVal * cur, sum, path + "*" + cur, num, target);
            }
        }
    }
}
