def addOperators(self, num, target):
    if not num: 
        return []

    def dfs(path, s):
        if not s :
            if eval(path) == target:
                paths.append(path)
        else:
            ops = ['+', '-', '*'] if s[0] == '0' else ['+', '-', '*', '']
            for op in ops:
                dfs(path + op + s[0], s[1:])

    paths = []
    dfs(num[0], num[1:])

    return paths
