# Intuition
Back track to form all the possible expressions. Don't prune as it might elminate possible solutions. Use stack to evaluate the expression.

# Approach
* multiplication operation has higher precedence than addion(+) or substraction(-)

# Code
```
import "strconv"
import "strings"
// -1 = +
// -2 = -
// -3 = *

func addOperators(num string, target int) []string {
	finalResults := []string{}
	number, _ := strconv.Atoi(num)
	expr := make([]int, len(num))
	l := len(num) - 1
	for number != 0 {
		expr[l] = number % 10
		l--
		//expr = append(expr, number%10)
		number = number / 10
	}
	//reverse := func(s string) string {
	//	rns := []rune(s) // convert to rune
	//	for i, j := 0, len(rns)-1; i < j; i, j = i+1, j-1 {
	//
	//		// swap the letters of the string,
	//		// like first with last and so on.
	//		rns[i], rns[j] = rns[j], rns[i]
	//	}
	//
	//	// return the reversed string.
	//	return string(rns)
	//}
	addToResults := func(expression []int) {
		var r strings.Builder
		for _, v := range expression {
			if v == -1 {
				r.WriteString("+")
			} else if v == -2 {
				r.WriteString("-")
			} else if v == -3 {
				r.WriteString("*")
			} else {
				r.WriteString(strconv.Itoa(v))
			}
		}
		finalResults = append(finalResults, r.String())
	}

	validExpression := func(expression []int, target int) int {
		stack := []int{}
		//fmt.Println("Expression : ", expression)
		for _, val := range expression {
			if val >= 0 && len(stack) > 0 && stack[len(stack)-1] == -3 {
				stack[len(stack)-2] = val * stack[len(stack)-2]
				stack = stack[:len(stack)-1]
			} else {
				stack = append(stack, val)
			}
		}

		if len(stack) > 1 {
			result := stack[0]
			operation := stack[1]
			for i := 2; i < len(stack); i++ {
				if stack[i] > 0 {
					if operation == -1 {
						result = result + stack[i]
					} else {
						result = result - stack[i]
					}
				} else {
					operation = stack[i]
				}
			}
			//fmt.Println("Result: ", result)
			return result
		}
		//fmt.Println("Result: ", stack[0])
		return stack[0]
	}

	var add func([]int, []int, int, int)
	add = func(num []int, expression []int, target int, index int) {
		if index == len(num) {
			if validExpression(expression, target) == target {
				addToResults(expression)
			}
		} else {
            // Add
            copyExp := make([]int, len(expression))
			copy(copyExp, expression)
			copyExp = append(copyExp, -1)
			copyExp = append(copyExp, num[index])
			add(num, copyExp, target, index+1)

            //Substract
			copyExp = make([]int, len(expression))
			copy(copyExp, expression)
			copyExp = append(copyExp, -2)
			copyExp = append(copyExp, num[index])
            add(num, copyExp, target, index+1)

            // Multiplication
			copyExp = make([]int, len(expression))
			copy(copyExp, expression)
			copyExp = append(copyExp, -3)
			copyExp = append(copyExp, num[index])
            add(num, copyExp, target, index+1)

            // Combine current digit with last operand
            if expression[len(expression)-1] != 0 {
                expression[len(expression)-1] = (expression[len(expression)-1]*10)+num[index];
                add(num, expression,target, index+1)
            }

		}
	}
	add(expr, []int{expr[0]}, target, 1)
	return finalResults
}
