		long long num = 0;
		string tmp;

		for(auto j = it; j < n; j++){
			if(j > it && s[it] == '0') break;

			num = num * 10 + (s[j] - '0');
			tmp.push_back(s[j]);

			if(it == 0) solve(j + 1, tmp, num, num);
			else{
				solve(j + 1, path + "+" + tmp, resSoFar + num, num);
				solve(j + 1, path + '-' + tmp, resSoFar - num, -num);
				solve(j + 1, path + '*' + tmp, resSoFar - prev + prev * num, prev * num);
			}
		}

	}
	vector<string> addOperators(string num, int target) {
		this -> target = target;
		s = num, n = num.size();

		solve(0, "", 0, 0);
		return res;
	}
};

