    def addOperators(self, num: str, T: int) -> List[str]:

		def f(i, exp, prev, expval):

			if i == len(num):
				if expval == T:
					self.res.append(exp)
				return

			for j in range(i, len(num)):
				cur = int(num[i:j+1])
				if num[i] == '0' and i < j: # 1* 05 = 1*5 is not allowed so 05 has to be skipped. IMP
					return

				if i == 0:
					f(j+1, str(cur), cur, cur) # IMP: 1st number will not use any operators. Each subsequent number will be <op> num
