class Solution:
    def addOperators(self, num: str, target: int) -> List[str]:

        # required = is there a leading 0 to the current expression?
        def backtracking(solution, pos, num, target, expression, required):
            if pos == len(num):
                S = 0
                operators = []
                for c in expression:
                    if not c.isnumeric():
                        operators.append(c)

                if len(operators) == 0:
                    if target == int(expression):
                        solution.append(expression)
                else:
                    oldExp = expression
                    for o in "*+-":
                        expression = expression.replace(o, " ")

                    values = expression.split()
                    j = len(values) - 1
                    k = len(operators) - 1
                    while j > 0: # and k >= 0:
                        if operators[k] == '+':
                            S += int(values[j])
                        elif operators[k] == '-':
                            S -= int(values[j])
                        else:
                            P = int(values[j])
                            k -= 1
                            j -= 1
                            while j > 0 and operators[k] == '*': # and k >= 0 :
                                P *= int(values[j])
                                j -= 1
                                k -= 1
                            P *= int(values[j])
                            if j == 0:
                                S += P
                            else:
                                if operators[k] == '-':
                                    S -= P
                                else:
                                    S += P
                        j -= 1
                        k -= 1
                    if j == 0:
                        S += int(values[0])
                    if S == target:
                        solution.append(oldExp)
            else:
                r = False
                if num[pos] == '0':
                    r = True

                if required == False:
                    backtracking(solution, pos + 1, num, target, expression + num[pos], False)
                backtracking(solution, pos + 1, num, target, expression + '+' + num[pos], r)
                backtracking(solution, pos + 1, num, target, expression + '-' + num[pos], r)
                backtracking(solution, pos + 1, num, target, expression + '*' + num[pos], r)


        solution = []
        if num[0] == '0':
            backtracking(solution, 1, num, target, num[0], True)
        else:
            backtracking(solution, 1, num, target, num[0], False)

        return solution
