var addOperators = function(num, target) {
    let ans = [];

    const backtracking = (index, left, right, string) => {
        if (index === num.length) {
            if (left + right === target)
                ans.push(string); 
            return;
        }

        let val = 0;
        for (let i = index; i < num.length; i++) {
            val = val * 10 + Number(num[i]);
            backtracking(i + 1, left + right, val, string + "+" + val);
            backtracking(i + 1, left + right, -val, string + "-" + val);
            backtracking(i + 1, left, right * val, string + "*" + val);
            if (num[index] === "0")
                break;
        }  
    };

    let val = 0;
    for (let i = 0; i < num.length; i++) {
        val = val * 10 + Number(num[i]);
        backtracking(i + 1, 0, val, val.toString());
        if (num[0] === "0")
            break;
    }

    return ans;
};

