class Solution {
public:
    void solve(vector<string> &ans,string currstr,string input,int t,int pos,long long currval,long long last)
    {
        if(pos==input.length())
        {
            if(currval==t)
                ans.push_back(currstr);
            return;
        }
        for(int i=pos;i<input.length();i++)
        {
            if(pos!=i && input[pos]=='0')
                break;
            string part=input.substr(pos,i+1-pos);
            long long curr=stoll(part);
            if(pos==0)
            {
                solve(ans,currstr+part,input,t,i+1,curr,curr);
            }
            else
            {
                solve(ans,currstr+"+"+part,input,t,i+1,currval+curr,curr);
                solve(ans,currstr+"-"+part,input,t,i+1,currval-curr,-curr);
                solve(ans,currstr+"*"+part,input,t,i+1,currval-last+last*curr,last*curr);
            }
        }
    }
    vector<string> addOperators(string num, int t) {
        vector<string> ans;
        solve(ans,"",num,t,0,0,0);
        return ans;
    }
};
