class Solution {
public:
    vector<string> addOperators(string num, int target) {
        int n = num.size(); 
        vector<string> ans; 

        function<void(int, long, long, string)> fn = [&](int i, long total, long last, string expr) {
            if (i == n) {
                if (total == target) ans.push_back(expr); 
            } else {
                for (int ii = i; ii < (num[i] == '0' ? i+1 : n); ++ii) {
                    string ss = num.substr(i, ii-i+1); 
                    long val = stol(ss); 
                    if (i == 0) fn(ii+1, val, val, ss); 
                    else {
                        fn(ii+1, total - last + last * val, last * val, expr + "*" + ss); 
                        fn(ii+1, total + val, val, expr + "+" + ss); 
                        fn(ii+1, total - val, -val, expr + "-" + ss); 
                    }
                }
            }
        }; 

        fn(0, 0, 0, ""); 
        return ans; 
    }
};
