class Solution {
    public List<String> addOperators(String num, int target) {
        List<String> result = new LinkedList<String>();
        helper(num, target, 0, 0, '!', 0, "", result);
        return result;
    }

    private void helper(String num, int target, long curSum, long prevNum, char prevOp, int pos, String local, List<String> result) {
        if (pos == num.length()) {
            if (curSum == (long)target) result.add(local);
            return ;
        }

        long curNum = 0;
        for(int i = pos; i < num.length(); i++) {
            if (i == pos || curNum > 0) {
                curNum = curNum*10 + Integer.valueOf(num.charAt(i)-'0');  
                if (pos == 0) {
                    helper(num, target, curNum, curNum, prevOp, i+1, String.valueOf(curNum), result);
                } else {
                    // +
                    helper(num, target, curSum+curNum, curNum, '+', i+1, local+"+"+curNum, result);
                    // -
                    helper(num, target, curSum-curNum, curNum, '-', i+1, local+"-"+curNum, result);
                    // *
                    if (prevOp == '-') {
                        helper(num, target, curSum+prevNum-(prevNum*curNum), prevNum*curNum, prevOp, i+1, local+"*"+curNum, result);
                    } else if (prevOp == '+') {
                        helper(num, target, curSum-prevNum+(prevNum*curNum), prevNum*curNum, prevOp, i+1, local+"*"+curNum, result);
                    } else {
                        helper(num, target, curSum*curNum, curNum, prevOp, i+1, local+"*"+curNum, result);    
                    }
                }   
            }
        }
    }
}
