        if(resLen == 0 || i == numLen || c == '*' || c == '+' || c == '-') {
            if(prevOp == '+') {
                sum += lOp;
                lOp = rOp;
            } else if(prevOp == '-') {
                sum += lOp;
                lOp = -rOp;
            } else {
                lOp = lOp * rOp;
            }
            rOp = 0;
            prevOp = c;            
