	void traverse(TreeNode* root, int bit) {
		if(!root) {
			return;
		}

		bit ^= (1 << root->val);
		if(!root->left && !root->right) {
			bitset<32> b(bit);
			if(b.count() <= 1)
				++ans;
		}
		traverse(root->left, bit);
		traverse(root->right, bit);
	}
};

