class Solution {
    public int[] findDiagonalOrder(int[][] matrix) {
        if(matrix.length == 0)
            return new int[0];

        int r = matrix.length, c = matrix[0].length, i =0, j =0;
        int[] ans = new int[r*c];

        for(int k=0; k<ans.length; k++){
            ans[k] = matrix[i][j];

            if((i+j)%2 == 0){
                if(j == c-1)
                    i++;
                else if(i == 0)
                    j++;
                else{
                    i--;
                    j++;
                }
            }else{
                if(i == r-1)
                    j++;
                else if(j == 0)
                    i++;
                else{
                    i++;
                    j--;
                }

            }
        }
        return ans;   
    }
}
