 #define rep(i,a,b) for(lli i=a;i<b;i++)
class Solution {
public:
    vector<int> fre;               //<-------vector to store the values which are present in curent path--> 
    int ans=0;                     //<-------store the answer---->

	//<------------------------DFS code starts------------->
    void dfs(TreeNode* node)
    {   if(node==NULL) return; 
        int value=node->val;
         fre[value]++;   //<---storing the value of node in "fre" variable--->
          if((node->left==NULL) && (node->right==NULL))// That means it is leaf node
          {
              int c=0;
              rep(i,1,10){
                  c+=(fre[i]%2!=0);
              }
              if(c<=1) ans++;
         }
          if(node->left!=NULL)    { dfs(node->left); }

          if(node->right!=NULL)  { dfs(node->right); }

     fre[value]--;
    }

    int pseudoPalindromicPaths (TreeNode* root) {
        rep(i,0,10) fre.pb(0);
        dfs(root);

        return ans;
    }
};
