# Definition for singly-linked list.
# class ListNode:
#     def __init__(self, val=0, next=None):
#         self.val = val
#         self.next = next
class Solution:

    def __init__(self, head: Optional[ListNode]):
        self.h = head

    def getRandom(self) -> int:

        curr = self.h
        count = 0
        res = 0

        while curr:
            count += 1
            if randint(1, count) == 1:
                res = curr.val
            curr = curr.next

        return res


