 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
private:
    ListNode* head;
public:
    Solution(ListNode* h) {
        head = h;
        srand(time(NULL));
    }

    int getRandom() {
        int count = 0, res = 0;
        ListNode* curr = head;
        while (curr != nullptr) {
            if (rand() % (++count) == 0)
                res = curr->val;
            curr = curr->next;
        }
        return res;
    }
};

