class Solution:
    def largestRectangleArea(self, heights: List[int]) -> int:

        if not heights:
            return 0

        N = len(heights)
        area=0
        nsr=[]
        nsl=[]
        i = N - 1
        stack=[]

        while i >= 0 :

            while stack and heights[stack[-1]] >= heights[i]:
                stack.pop()
            if not stack:
                stack.append(i)
                nsr.append(N)
            else:
                nsr.append(stack[-1])
                stack.append(i)
            i-=1

        nsr = nsr[::-1]

        stack=[]
        i = 0
        while i < N :

            while stack and heights[stack[-1]] > heights[i]:
                stack.pop()
            if not stack:
                stack.append(i)
                nsl.append(-1)
            else:
                nsl.append(stack[-1])
                stack.append(i)
            i+=1
        for i in range(N):
            area = max(area,(nsr[i]+(-nsl[i]-1))*heights[i])
        return area
```

