public int[] sortedSquares(int[] nus) {
        int n = nums.length, i = 0, j = n - 1;
        int[] result = new int[n];
        for (int p = n - 1; p >= 0; p--) {
            if (Math.abs(nums[i]) > Math.abs(nums[j])) {
                result[p] = nums[i] * nums[i];
                i++;
            } else {
                result[p] = nums[j] * nums[j];
                j--;
            }
        }
        return result;
    }
